import React from "react";
import Chart from "../Chart/Chart";
const ExpensesChart = (props) => {
  const chartDataPoint = [
    { label: "January", value: "{props.expenses.amount}" },
    { label: "February", value: "{props.expenses.amount}" },
    { label: "March", value: "{props.expenses.amount}" },
    { label: "April", value: "{props.expenses.amount}" },
    { label: "May", value: "{props.expenses.amount}" },
    { label: "June", value: "{props.expenses.amount}" },
    { label: "July", value: "{props.expenses.amount}" },
    { label: "August", value: "{props.expenses.amount}" },
    { label: "September", value: "{props.expenses.amount}" },
    { label: "October", value: "{props.expenses.amount}" },
    { label: "November", value: "{props.expenses.amount}" },
    { label: "December", value: "{props.expenses.amount}" },
  ];
  for (const expense of props.expenses) {
    const expenseMonth = expense.date.getMonth();
    chartDataPoint[expenseMonth].value += expense.amount;
  }
  return (
    <>
      <Chart dataPoints={chartDataPoint} />
    </>
  );
};
export default ExpensesChart;
